package Jitflix.Jitflix.auth;import lombok.RequiredArgsConstructor;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@RestController@RequestMapping("/api/v1/auth")@RequiredArgsConstructorpublic class AuthController {    private final AuthService service;    @PostMapping("/register")    public ResponseEntity<AuthenticationResponse> register(            @RequestBody RegistrationRequest registrationRequest) {        return ResponseEntity.ok(service.register(registrationRequest));    }    @PostMapping("/authenticate")    public ResponseEntity<AuthenticationResponse> register(            @RequestBody AuthenticationRequest authenticationRequest) {        return ResponseEntity.ok(service.authenticate(authenticationRequest));    }}