package Jitflix.Jitflix.controller;import jakarta.servlet.http.Cookie;import jakarta.servlet.http.HttpServletRequest;import jakarta.servlet.http.HttpServletResponse;import jakarta.servlet.http.HttpSession;import org.springframework.http.ResponseEntity;import org.springframework.security.core.Authentication;import org.springframework.security.core.context.SecurityContextHolder;import org.springframework.security.web.authentication.logout.SecurityContextLogoutHandler;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@RestController@RequestMapping("/api/v1")public class LogoutController {    @PostMapping("/logout")    public ResponseEntity<?> logout(HttpServletRequest request,                                    HttpServletResponse response) {        Authentication auth = SecurityContextHolder.getContext()                .getAuthentication();        if (auth != null) {            new SecurityContextLogoutHandler().logout(request, response, auth);        }        HttpSession session = request.getSession(false);        if (session != null) {            session.invalidate();        }        Cookie accessToken = new Cookie("access_token", null);        accessToken.setMaxAge(0);        Cookie refreshToken = new Cookie("refresh_token", null);        refreshToken.setMaxAge(0);        response.addCookie(accessToken);        response.addCookie(refreshToken);        return ResponseEntity.ok("Logged out successfully");    }}