package Jitflix.Jitflix.entity.mongo;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import org.bson.types.ObjectId;import org.springframework.data.annotation.Id;import org.springframework.data.mongodb.core.mapping.Document;import org.springframework.data.mongodb.core.mapping.Field;import java.util.Date;@Getter@Setter@NoArgsConstructor@Document(collection = "comments")public class Comment {    @Id    private String id;    private String name;    private String email;    @Field("movie_id")    private ObjectId movieId;    private String text;    private Date date;}