package Jitflix.Jitflix.entity.pg;import com.fasterxml.jackson.annotation.JsonFormat;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import org.hibernate.annotations.JdbcTypeCode;import org.hibernate.type.SqlTypes;import java.util.Date;import java.util.Set;@Entity@Getter@Setter@AllArgsConstructor@Table(name = "video_call")@NoArgsConstructorpublic class VideoCall {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "id", nullable = false)    @JdbcTypeCode(SqlTypes.LONGVARCHAR)    private Long id;    @ManyToOne    @JoinColumn(name = "created_by_user_id")    private AppUser createdBy;    @ManyToMany    @JoinTable(            name = "video_call_participants",            joinColumns = @JoinColumn(name = "video_call_id"),            inverseJoinColumns = @JoinColumn(name = "participant_id")    )    private Set<AppUser> participants;    @JsonFormat(shape = JsonFormat.Shape.STRING,                pattern = "yyyy-MM-dd'T'HH:mm:ss")    private Date validUntil;    @JsonFormat(shape = JsonFormat.Shape.STRING,                pattern = "yyyy-MM-dd'T'HH:mm:ss")    private Date createdAt;    public void setParticipants(Set<AppUser> participants) {        this.participants.addAll(participants);    }    public void setParticipants(AppUser participants) {        this.participants.add(participants);    }}