package Jitflix.Jitflix.exception;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.ExceptionHandler;import org.springframework.web.bind.annotation.RestControllerAdvice;import javax.naming.AuthenticationException;@RestControllerAdvicepublic class GlobalExceptionHandler {    @ExceptionHandler(Exception.class)    public ResponseEntity<String> handleExpiredJwtException(            Exception e) {        if (e.getMessage().equals("jwt expired")) {            return new ResponseEntity<>("Token Expired",                    HttpStatus.UNAUTHORIZED);        } else if (e.getMessage().equals("jwt malformed")) {            return new ResponseEntity<>("Token malformed",                    HttpStatus.UNAUTHORIZED);        } else if (e instanceof AuthenticationException) {            return new ResponseEntity<>("Unauthorized",                    HttpStatus.UNAUTHORIZED);        } else {            return new ResponseEntity<>("Internal Server Error",                    HttpStatus.INTERNAL_SERVER_ERROR);        }    }}