package Jitflix.Jitflix.service.database;import Jitflix.Jitflix.entity.mongo.Comment;import Jitflix.Jitflix.repository.mongo.CommentRepository;import lombok.AllArgsConstructor;import org.bson.types.ObjectId;import org.springframework.stereotype.Service;import java.util.List;@Service@AllArgsConstructorpublic class CommentService {    private final CommentRepository commentRepository;    public List<Comment> getCommentsByMovieId(String movie_id) {        return commentRepository.findByMovieId(new ObjectId(movie_id));    }}