package Jitflix.Jitflix.service.database;import Jitflix.Jitflix.dto.UserDTO;import Jitflix.Jitflix.dto.ViewingHistoryDTO;import Jitflix.Jitflix.entity.pg.AppUser;import Jitflix.Jitflix.entity.pg.ViewingHistory;import org.springframework.stereotype.Service;@Servicepublic class EntityToDtoConverter {    public UserDTO convertToDto(AppUser appUser) {        UserDTO userDTO = new UserDTO();        userDTO.setUserId(appUser.getUserId());        userDTO.setFirstName(appUser.getFirstName());        userDTO.setLastName(appUser.getLastName());        userDTO.setEmail(appUser.getEmail());        userDTO.setLanguage(appUser.getLanguage());        userDTO.setCountry(appUser.getCountry());        return userDTO;    }    public ViewingHistoryDTO convertToDto(ViewingHistory history) {        ViewingHistoryDTO viewingHistoryDTO = new ViewingHistoryDTO();        viewingHistoryDTO.setHistoryId(history.getHistoryId());        viewingHistoryDTO.setMovieId(history.getMovieId());        viewingHistoryDTO.setViewTimestamp(history.getViewTimestamp());        viewingHistoryDTO.setLastStoppedMinute(history.getLastStoppedMinute());        viewingHistoryDTO.setUser(convertToDto(history.getAppUser()));        return viewingHistoryDTO;    }}